package program;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		(&&) (1.5) (1-5)-------------NOT USED FOR LOOPING
		
		for(int i=1;i<=5;i++) {
			
			if(i%2==0) {
				
				
				System.out.println("Even: "+i);
			
				
			}
			else if(i%2==1) {
				
				System.out.println("Odd :"+i);
			}
		}

	}

}
